INSERT INTO `product_option` VALUES (491, 502, 'DrayTek Vigor2925Fn', '<p>Router-Wifi Dual-WAN Load Balance v&agrave; VPN đa k&ecirc;nh cho doanh nghiệp.</p>\r\n\r\n<p>- 2 cổng Gigabit Ethernet WAN, WAN 1: slot SFP 100/1000Mbps, WAN 2: 10/100/1000 Mbps RJ45.<br />\r\n- 4 cổng Gigabit Ethernet LAN 10/100/1000Mbps, RJ45. 2 cổng USB cho ph&eacute;p kết nối USB 3G/4G, Printer...<br />\r\n- 2 Anten băng tần 2.4GHz, tốc độ 300Mbps với chuẩn 802.11n. Tạo tối đa 4 SSID.<br />\r\n- Dual-WAN Load Balance/Failover, hỗ trợ đường truyền đa dịch vụ: IPTV, Internet, VoIP...<br />\r\n&nbsp;</p>', 'draytek-vigor2925fn.html', 'modem-router', '1582863982.DrayTek_Vigor2925Fn_4.jpg', 4590000, NULL, 2, NULL, 'admin', 0, '1', NULL, 100, '2020-02-28 10:51:58', '2021-01-24 00:21:15', '1');
