INSERT INTO `statics` VALUES (1, 'Giới thiệu', '<p>----------------------------------------------------------------------------------------------------------------------------<br />\r\n<strong>I. &nbsp;GIỚI THIỆU CHUNG</strong></p>\r\n\r\n<p>- &nbsp;T&ecirc;n c&ocirc;ng ty: C&Ocirc;NG TY TNHH T&Iacute;CH HỢP HỆ THỐNG VINA</p>\r\n\r\n<p>- &nbsp;T&ecirc;n tiếng anh: VINA INTEGRATED SYSTEM</p>\r\n\r\n<p>- &nbsp;T&ecirc;n viết tắt: VNIS</p>\r\n\r\n<p>- &nbsp;Trụ sở ch&iacute;nh: 3/25/5A1, Đường 182, P.Tăng Nhơn Ph&uacute; A, Th&agrave;nh Phố Thủ Đức, TP.HCM</p>\r\n\r\n<p>- &nbsp;Chi nh&aacute;nh: 38C Trần Thị Điệu,&nbsp;P.Phước Long B, TP.Thủ Đức, TP.HCM</p>\r\n\r\n<p>- MST: 03 09 55 48 60</p>\r\n\r\n<p>- &nbsp;Điện thoại: (84-8) 62987871 | Fax: (84-8) 62987860</p>\r\n\r\n<p>-&nbsp; Mobile: 093.7777.638 | 090.9747.235</p>\r\n\r\n<p>- &nbsp;Email:&nbsp; info@vinacenter.vn</p>\r\n\r\n<p>- &nbsp;Website: www.vinacenter.vn</p>\r\n\r\n<p><strong>1. &nbsp;TH&Ocirc;NG ĐIỆP GI&Aacute;M ĐỐC</strong></p>\r\n\r\n<p>- &nbsp;Bắt đầu năm 2009 C&ocirc;ng Ty VINA đ&atilde; đặt những vi&ecirc;n gạch nền m&oacute;ng đầu ti&ecirc;n trong lĩnh vực kinh doanh t&iacute;ch hợp hệ thống. Với mục ti&ecirc;u trở th&agrave;nh đơn vị t&iacute;ch hợp chuy&ecirc;n nghiệp.Tr&ecirc;n cơ sở định hướng chiến lược d&agrave;i hạn về c&ocirc;ng nghệ , sản phẩm, dịch vụ v&agrave; nh&acirc;n lực VINA đ&atilde; nhanh ch&oacute;ng trở th&agrave;nh Nh&agrave; cung cấp giải ph&aacute;p v&agrave; T&iacute;ch hợp h&agrave;ng đầu tại Việt Nam.</p>\r\n\r\n<p>- &nbsp;Tuy mới th&agrave;nh lập nhưng với đội ngũ nh&acirc;n vi&ecirc;n l&agrave; c&aacute;c kỹ sư gi&agrave;u kinh nghiệm, năng động s&aacute;ng tạo v&agrave; chuy&ecirc;n nghiệp, VINA đ&atilde; cung cấp một tập hợp c&aacute;c sản phẩm v&agrave; dịch vụ phong ph&uacute; , đa dạng từ cung cấp c&aacute;c thiết bị CNTT, tư vấn x&acirc;y dựng giải ph&aacute;p, đến triển khai giải ph&aacute;p tổng thể cho c&aacute;c hệ thống th&ocirc;ng tin điện tử, dịch vụ đ&agrave;o tạo v&agrave; chuyển giao c&ocirc;ng nghệ ti&ecirc;n tiến.</p>\r\n\r\n<p>- &nbsp;Với phương ch&acirc;m &ldquo; Giải ph&aacute;p tin cậy, Dịch vụ ho&agrave;n hảo, C&ocirc;ng nghệ ti&ecirc;n tiến&rdquo;, VINA cam kết giữ vững l&agrave; nh&agrave; t&iacute;ch hợp hệ thống h&agrave;ng đầu tự tin vươn xa .Niềm tin v&agrave; sự ghi nhận của mỗi đơn vị v&agrave; của cộng đồng l&agrave; động lực to lớn gi&uacute;p ch&uacute;ng t&ocirc;i tiếp tục đ&oacute;ng g&oacute;p những lợi &iacute;ch trực tiếp v&agrave; gi&aacute;n tiếp cho việc ph&aacute;t triển đất nước Việt Nam của ch&uacute;ng ta ng&agrave;y c&agrave;ng thịnh vượng.</p>\r\n\r\n<p><strong>2.&nbsp; GI&Aacute; TRỊ CỐT L&Otilde;I &ndash; THẾ MẠNH &amp; LỢI THẾ CẠNH TRANH &ndash; CHIẾN LƯỢC PH&Aacute;T TRIỂN TỪ 2010 - 2020.</strong></p>\r\n\r\n<p>- &nbsp;Gi&aacute; trị cốt l&otilde;i : Điều l&agrave;m n&ecirc;n sự kh&aacute;c biệt v&agrave; l&agrave; gi&aacute; trị cốt l&otilde;i của VINA l&agrave; C&ocirc;ng Nghệ, trong suốt những năm qua, VINA đ&atilde; đầu tư rất nhiều cho nghi&ecirc;n cứu v&agrave; ph&aacute;t triển, đến nay c&oacute; rất nhiều những sản phẩm v&agrave; dịch vụ của VINA c&oacute; gi&aacute; trị.</p>\r\n\r\n<p>- &nbsp;Thế mạnh v&agrave; lợi thế cạnh tranh :Với đội ngũ c&aacute;n bộ c&oacute; năng lực v&agrave; kinh nghiệm đ&atilde; l&agrave;m cho VINA lu&ocirc;n lu&ocirc;n ti&ecirc;n phong v&agrave; chiếm ưu thuế trong c&aacute;c giải ph&aacute;p CNTT cho c&aacute;c cơ quan.</p>\r\n\r\n<p>- &nbsp;C&oacute; nhiều kinh nghiệm trong triển khai v&agrave; cung cấp c&aacute;c giải ph&aacute;p, dịch vụ CNTT.</p>\r\n\r\n<p>- &nbsp;Thường xuy&ecirc;n nhi&ecirc;n cứu s&acirc;u về nhu cầu nghiệp vụ của kh&aacute;ch h&agrave;ng , &aacute;p dụng hiểu biết của c&aacute;c chuy&ecirc;n gia ng&agrave;nh h&agrave;ng đầu, ứng dụng c&ocirc;ng nghệ hiện đại để x&acirc;y dựng c&aacute;c giải ph&aacute;p thiết thực, b&aacute;m s&aacute;t nhu cầu thực tế.</p>\r\n\r\n<p>- &nbsp;C&oacute; h&agrave;ng trăm chuy&ecirc;n vi&ecirc;n được đ&agrave;o tạo b&agrave;i bản chuy&ecirc;n m&ocirc;n cũng như quản l&yacute;. Được cấp c&aacute;c bằng cấp cao nhất của c&aacute;c đối t&aacute;c c&ocirc;ng nghệ h&agrave;ng đầu thế giới như : Micorosoft, cisco. IBM, &hellip;</p>\r\n\r\n<p>- &nbsp;C&oacute; hệ thống ph&oacute;ng th&iacute; nghiệm c&ocirc;ng nghệ cao về hạ tầng mạng, trung t&acirc;m dữ liệu, hệ thống bảo mật.</p>\r\n\r\n<p>- &nbsp;Nhận được sự hỗ trợ mạnh từ c&aacute;c đối t&aacute;c h&agrave;ng đầu tr&ecirc;n thế giới trong lĩnh vực t&iacute;ch hợp hệ thống , VINA hiện nay l&agrave; đối t&aacute;c của Microsoft&hellip;.</p>\r\n\r\n<p>Với thế mạnh tr&ecirc;n c&ugrave;ng với việc lu&ocirc;n ch&uacute; trọng n&acirc;ng cao tỷ trọng cung cấp giải ph&aacute;p v&agrave; dịch vụ c&oacute; h&agrave;m lượng c&ocirc;ng nghệ cao , trong những năm qua VINA lu&ocirc;n chiếm lĩnh thị trường trọng điểm c&oacute; mức đầu tư v&agrave; ứng dụng CNTT lớn.</p>\r\n\r\n<p>- &nbsp;Chiến lược ph&aacute;t triển : Ph&aacute;t huy thế mạnh m&agrave; một trong những c&ocirc;ng ty cung cấp giải ph&aacute;p v&agrave; dịch vụ t&iacute;ch hợp h&agrave;ng đầu Việt Nam, VINA đang ph&aacute;t&nbsp; theo hai hướng :</p>\r\n\r\n<p>+ Một l&agrave; : Đầu tư mạnh mẽ v&agrave;o c&aacute;c thị trường c&oacute; nhu cầu lớn, chuy&ecirc;n nghiệp v&agrave; phức tạp về CNTT như : T&agrave;i Ch&iacute;nh, Ng&acirc;n H&agrave;ng, Bảo Hiểm, Chứng Kho&aacute;n, Gi&aacute;o Dục,...</p>\r\n\r\n<p>+ Hai l&agrave; : Triển khai c&aacute;c dịch vụ hạ tầng v&agrave; giải ph&aacute;p chuy&ecirc;n nghiệp phục vụ theo chiều ngang tới cả những kh&aacute;ch h&agrave;ng như c&aacute;c doanh nghiệp lớn, khối ch&iacute;nh phủ, dịch vụ c&ocirc;ng cộng, Gi&aacute;o dục v&agrave; Đ&agrave;o tạo.</p>\r\n\r\n<p>- &nbsp;VINA với tư c&aacute;ch l&agrave; đơn vị t&iacute;ch hợp hệ thống , sẽ l&agrave; đầu mối của cả tập đo&agrave;n c&ocirc;ng nghệ th&ocirc;ng tin để cung cấp những giải ph&aacute;p tổng thể v&agrave; trọn g&oacute;i cho kh&aacute;ch h&agrave;ng.</p>\r\n\r\n<p>- &nbsp;X&acirc;y dựng v&agrave; ph&aacute;t triển t&iacute;nh chuy&ecirc;n nghiệp của lực lượng nh&acirc;n sự tr&ecirc;n nền năng lực của c&aacute;c nh&acirc;n vi&ecirc;n c&oacute; tr&igrave;nh độ cao, được huấn luyện v&agrave; đ&agrave;o tạo b&agrave;i bản trong nội b&ocirc; của c&aacute;c đối t&aacute;c.</p>\r\n\r\n<p>- &nbsp;&Aacute;p dụng những kinh nghiệm đ&atilde; t&iacute;ch lũy trong nhiều năm triển khai nhiều dự &aacute;n th&agrave;nh c&ocirc;ng, VINA sẽ lu&ocirc;n đem lại cho kh&aacute;ch h&agrave;ng sự cập nhật mới nhất , hợp l&yacute; nhất về c&ocirc;ng nghệ v&agrave; giải ph&aacute;p.</p>', '2022-08-27 14:46:49');
