INSERT INTO `vnc_product` VALUES (231, 'Dell Inspiron 3567', 'dell-inspiron-3567', '<p><span style=\"font-size:18px\"><strong><a href=\"http://www.vinacenter.net/san-pham/dell-inspiron-3567.html\">Laptop Dell Inspiron 3567</a> N3567S - P63F002</strong></span></p>\r\n\r\n<p><span style=\"color:#003399\"><strong><span style=\"font-size:20px\">- Thiết kế bền đẹp, chắc chắn</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"http://www.vinacenter.net/san-pham/dell-inspiron-3567.html\">Laptop Dell Inspiron 3567</a> N3567S - P63F002 được x&acirc;y dựng bền vững với những khớp nối v&agrave; bản lề vững chắc, cho m&aacute;y độ bền v&agrave; khả năng chống va đập cao. M&agrave;u đen tuyền cho thấy sự mạnh mẽ, nổi bật đầy ấn tượng.</span></p>\r\n\r\n<p><img alt=\"\" src=\"http://vinacenter.net/public/uploads/images/11207/11021701/thiet-ke.png\" style=\"float:left; height:500px; width:1500px\" /><br />\r\n<span style=\"color:#003399\"><span style=\"font-size:20px\"><strong>- M&agrave;n h&igrave;nh 15.6 inch v&agrave; c&ocirc;ng nghệ &acirc;m thanh Waves MaxxAudio&reg; Pro</strong></span></span><br />\r\n<span style=\"font-size:14px\"><a href=\"http://www.vinacenter.net/san-pham/dell-inspiron-3567.html\">Dell Inspiron 15 3567</a> được trang bị m&agrave;n h&igrave;nh 15.6 inch cho h&igrave;nh ảnh s&aacute;ng, đẹp r&otilde; n&eacute;t thỏa sức để bạn chơi game hay xem c&aacute;c bộ phim đặc sắc. Một m&agrave;n h&igrave;nh lớn đẹp, mịn m&agrave;ng kết hợp th&ecirc;m c&ocirc;ng nghệ &acirc;m thanh Waves MaxxAudio&reg; Pro cực to, sống động mang lại cho bạn kh&ocirc;ng gian giải tr&iacute; đ&uacute;ng nghĩa.</span></p>\r\n\r\n<p><img alt=\"\" src=\"http://vinacenter.net/public/uploads/images/11207/11021701/Man-hinh-game.png\" style=\"float:left; height:500px; width:1500px\" /><br />\r\n<span style=\"color:#003399\"><span style=\"font-size:20px\"><strong>- Hiệu năng ấn tượng trong tầm gi&aacute;</strong></span></span><br />\r\n<span style=\"font-size:14px\"><a href=\"http://www.vinacenter.net/san-pham/dell-inspiron-3567.html\">Dell Inspiron 15 3567</a> sở hữu cấu h&igrave;nh tốt trong tầm gi&aacute; bao gồm bộ vi xử l&yacute; Intel&reg; Core&trade; i3-7020U Processor , Ram 4GB DDR4 2400MHz gi&uacute;p chạy nhiều t&aacute;c vụ c&ugrave;ng l&uacute;c một c&aacute;ch mượt m&agrave;. B&ecirc;n cạnh đ&oacute;, card đồ họa Intel HD Graphics 620 đủ đ&aacute;p ứng cho c&aacute;c t&aacute;c vụ cơ bản trong học tập cũng như văn ph&ograve;ng. Với cấu h&igrave;nh n&agrave;y, Dell Inspiron 15 3567 chắc chắn sẽ trở th&agrave;nh một c&ocirc;ng cụ hữu &iacute;ch cho bạn, trong cả c&ocirc;ng việc v&agrave; giải tr&iacute;.<br />\r\nVề kh&ocirc;ng gian lưu trữ, <a href=\"http://www.vinacenter.net/san-pham/dell-inspiron-3567.html\">Dell Inspiron 15 3567</a> c&oacute; ổ cứng HDD 1TB, một dung lượng thoải m&aacute;i để bạn lưu trữ t&agrave;i liệu, tệp, phim v&agrave; c&aacute;c game y&ecirc;u th&iacute;ch.<br />\r\nKhả năng kết nối<br />\r\nLaptop <a href=\"http://www.vinacenter.net/san-pham/dell-inspiron-3567.html\">Dell Inspiron 3567 N3567S</a> - P63F002 sở hữu đầy đủ c&aacute;c cổng kết nối cơ bản nhất hiện nay đảm bảo khả năng kết nối trao đổi dữ liệu với c&aacute;c thiết bị kh&aacute;c. Đặc biệt, m&aacute;y c&ograve;n c&oacute; ổ đĩa DVD-RW c&oacute; thể đọc v&agrave; ghi đĩa một c&aacute;ch đầy chuy&ecirc;n nghiệp.</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><span style=\"color:#003399\">- B&agrave;n ph&iacute;m ti&ecirc;u chuẩn v&agrave; Touchpad đa cử chỉ</span></strong></span><br />\r\n<span style=\"font-size:14px\">Mặc d&ugrave; k&iacute;ch thước gọn g&agrave;ng nhưng Dell Inspiron 153567 vẫn c&oacute; b&agrave;n ph&iacute;m k&iacute;ch thước ti&ecirc;u chuẩn với c&aacute;c ph&iacute;m bố tr&iacute; khoảng c&aacute;ch hợp l&yacute;, độ nảy cao, phản hồi tốt cho bạn tốc độ g&otilde; ph&iacute;m nhanh v&agrave; &ecirc;m &aacute;i nhất.<br />\r\n<br />\r\n<img alt=\"\" src=\"http://vinacenter.net/public/uploads/images/11207/11021701/ban-phim-cong-ket-noi.png\" style=\"height:500px; width:1500px\" /></span><br />\r\n<br />\r\n<span style=\"font-size:14px\">- Touchpad tr&ecirc;n <a href=\"http://www.vinacenter.net/san-pham/dell-inspiron-3567.html\">Dell Inspiron 15 3567 </a>đ&atilde; được cải tiến đ&aacute;ng kể với độ rộng được cải thiện v&agrave; c&aacute;c thao t&aacute;c được hỗ trợ đầy đủ, thực hiện rất ch&iacute;nh x&aacute;c, thoải m&aacute;i hơn rất nhiều.<br />\r\n- Th&ocirc;ng tin th&ecirc;m: Ankhang l&agrave; thương hi&ecirc;̣u chuy&ecirc;n cung c&acirc;́p các dịch vụ bán lẻ máy tính bàn, laptop chính hãng của các thương hi&ecirc;̣u lớn tr&ecirc;n th&ecirc;́ giới, bạn cũng có th&ecirc;̉ tìm th&acirc;́y nhi&ecirc;̀u phụ ki&ecirc;̣n khác n&ecirc;n mua kèm như: USB, Modem wifi, balo, loa laptop, các ph&acirc;̀n m&ecirc;̀m bản quy&ecirc;̀n, tai nghe hay đ&ecirc;́ tản nhi&ecirc;̣t.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>', 14, 'dell', 1, 1, 'admin', '2019-07-16 17:00:46', '2019-07-16 18:39:52', NULL, NULL);
