INSERT INTO `vnc_product` VALUES (242, 'Linksys LGS326P 24-Port Gigabit PoE', 'linksys-lgs326p-24-port-gigabit-poe', '<p>Th&ocirc;ng số kỹ thuật<br />\r\nD&ograve;ng sản phẩm:&nbsp; managed switch, layer 2. managed switch<br />\r\nCổng kết nối: 24 cổng gigabit&nbsp; (PoE+), 2 nh&oacute;m cổng kết hợp RJ45 +&nbsp; SFP.&nbsp; &nbsp; &nbsp; &nbsp;<br />\r\nHiệu suất switch: 52 Gbps.<br />\r\nTốc độ chuyển g&oacute;i tin: 38.69 Mpps (mega packet per second).<br />\r\nT&iacute;nh năng nổi bật: PoE+ 192W, Spanning Tree, VLAN hỗ trợ 128 kết nối, MAC Address 8K, Jumbo Frame (FE, GE) 9K, Port Mirroring, Storm Control,&nbsp; RMON, QoS, v..v..&nbsp;<br />\r\nCơ chế x&aacute;c thực: 802.1x Radius.<br />\r\nK&iacute;ch thước: 1U (44 x 20 x 4,4 cm), nặng 3,1 kg.</p>\r\n\r\n<p><img alt=\"\" src=\"http://vinacenter.net/public/uploads/files/ASUS/Linksys%20LGS326P%2024-Port%20Gigabit%20PoE-3.png\" style=\"height:500px; width:1500px\" /></p>\r\n\r\n<p>Linksys LGS326P l&agrave; thuộc d&ograve;ng managed switch layer 2 hiệu suất cao, hỗ trợ cả IPv4 v&agrave; IPv6. Thiết kế sản phẩm theo chuẩn 1U, đi k&egrave;m phụ kiện để c&oacute; thể dễ d&agrave;ng gắn v&agrave;o tủ rack. C&aacute;c khe tho&aacute;t nhiệt ở cạnh b&ecirc;n v&agrave; được 2 quạt l&agrave;m m&aacute;t gi&uacute;p việc tản nhiệt hiệu quả hơn, sản phẩm kh&ocirc;ng qu&aacute; n&oacute;ng khi hoạt động trong suốt qu&atilde;ng thời gian d&agrave;i.</p>', 72, 'hub-switch', 1, 1, 'admin', '2019-07-17 00:35:45', '2019-07-17 00:35:45', NULL, NULL);
