INSERT INTO `vnc_product` VALUES (478, 'IPC-HFW1430SP', 'ipc-hfw1430sp', '<p>&bull;&nbsp; Độ ph&acirc;n giải 4 Megapixel cảm biến CMOS k&iacute;ch thước 1/2.7&rdquo;, 20fps@4M<br />\r\n&bull;&nbsp; Hỗ trợ m&atilde; h&oacute;a 2 luồng với định dạng H.265 v&agrave; H.264<br />\r\n&bull;&nbsp; Chế độ ng&agrave;y đ&ecirc;m (ICR), Chống ngược s&aacute;ng DWDR, tự động c&acirc;n bằng trắng (AWB), tự động b&ugrave; s&aacute;ng (AGC), chống ngược s&aacute;ng(BLC), chống nhiễu (3DNR).<br />\r\n&bull;&nbsp; Hỗ trợ xem h&igrave;nh bằng nhiều c&ocirc;ng cụ: Web, phần mềm CMS (DSS/PSS) v&agrave; DMSS<br />\r\n&bull;&nbsp; Ống k&iacute;nh cố định 3.6mm, chuẩn chống nước IP67, DC12V hoặc PoE (802.3af), c&ocirc;ng suất 6W</p>', 60, 'dahua', 1, 1, 'admin', '2019-10-15 16:57:21', '2020-04-26 14:56:01', NULL, NULL);
INSERT INTO `vnc_product` VALUES (480, 'NVR2108HS-4KS2', 'nvr2108hs-4ks2', '<p>&bull; Đầu ghi h&igrave;nh 4/8/16 k&ecirc;nh camera IP,&nbsp; chuẩn n&eacute;n h&igrave;nh ảnh H.265/H.264 với hai luồng dữ liệu hỗ trợ hiển thị 1CH 8M v&agrave; 4CH 1080P , băng th&ocirc;ng đầu v&agrave;o max 80Mpb, hỗ trợ l&ecirc;n đến camera 8MP, hỗ trợ kết nối nhiều thương hiệu camera với chuẩn tương th&iacute;ch Onvif 2.4<br />\r\n&bull; Hỗ trợ 1 ổ cứng 6TB, 2 usd 2.0, 1 cổng RJ4(10/100M), 1 cổng audio v&agrave;o ra hỗ trợ đ&agrave;m thoại 2 chiều, hỗ trợ điều khiển quay qu&eacute;t th&ocirc;ng minh với giao thức dahua,&nbsp; hỗ trợ xem lại v&agrave; trực tiếp qua mạng m&aacute;y t&iacute;nh, thiết bị di động<br />\r\n&bull; Hỗ trợ cấu h&igrave;nh th&ocirc;ng minh qua P2P, quản l&yacute; đồng thời 128 t&agrave;i khoản kết nối, chất liệu kim loại.</p>', 60, 'dahua', 1, 1, 'admin', '2019-10-15 17:09:28', '2019-10-29 16:44:23', NULL, NULL);
