INSERT INTO `vnc_product` VALUES (596, 'Bàn phím Logitech G413 Carbon Mechanical Backlit', 'ban-phim-logitech-g413-carbon-mechanical-backlit', '<!DOCTYPE html>\r\n<html>\r\n<head>\r\n</head>\r\n<body>\r\n<p><strong>Switch Romer-G</strong></p>\r\n<p>- Ph&iacute;m switch cơ học Romer-G&trade;Tactile c&oacute; h&agrave;nh tr&igrave;nh ngắn chỉ 1,5 mm&mdash;nhanh hơn 25% Khoảng c&aacute;ch h&agrave;nh tr&igrave;nh 1,5 mm so với 2,0 mm của c&aacute;c ph&iacute;m cơ học ti&ecirc;u chuẩnso với c&aacute;c ph&iacute;m cơ học ti&ecirc;u chuẩn</p>\r\n<p><strong>Led nổi bật</strong></p>\r\n<p>- Được trang bị dải led 1 m&agrave;u đỏ nổi bật, G413 ho&agrave;n to&agrave;n th&iacute;ch hợp cho việc chơi game trong điều kiện thiếu &aacute;nh s&aacute;ng, đặc biệt l&agrave; buổi đ&ecirc;m</p>\r\n<p><strong>Vật liệu cao cấp</strong></p>\r\n<p>- Lớp vỏ tr&ecirc;n c&ugrave;ng bằng hợp kim nh&ocirc;m-magi&ecirc; 5052 l&agrave; bộ khung của b&agrave;n ph&iacute;m. Kết quả l&agrave; thiết kế đẹp mắt c&acirc;n bằng tất cả c&aacute;c t&iacute;nh năng</p>\r\n<p><strong>Cổng USB t&iacute;ch hợp</strong></p>\r\n<p>- C&aacute;p USB bổ sung, d&agrave;nh ri&ecirc;ng kết nối cổng truyền nhận qua USB với thiết bị đầu v&agrave;o cho 100% điện năng v&agrave; tốc độ truyền dữ liệu. Sử dụng cổng tr&ecirc;n G413 tương tự như cổng USB kh&aacute;c.</p>\r\n<p><strong>Ph&iacute;m đa phương tiện</strong></p>\r\n<p>- Điều khiển &acirc;m thanh, dừng, ph&aacute;t c&aacute;c b&agrave;i nhạc 1 c&aacute;ch nhanh ch&oacute;ng với c&aacute;c ph&iacute;m media được t&iacute;ch hợp để sử dụng với ph&iacute;m FN</p>\r\n</body>\r\n</html>', 87, 'ban-phim-logitech', 1, 1, 'VINA', '2021-12-29 15:18:12', '2021-12-29 15:18:12', NULL, NULL);
