INSERT INTO `vnc_product` VALUES (654, 'Chuột Logitech M650L', 'chuot-logitech-m650l', '<!DOCTYPE html>\r\n<html>\r\n<head>\r\n</head>\r\n<body>\r\n<p>- Loại chuột: Văn ph&ograve;ng</p>\r\n<p>- M&agrave;u sắc: Đen</p>\r\n<p>- Kiểu kết nối: Kh&ocirc;ng d&acirc;y</p>\r\n<p>- Loại kết nối: Đầu thu USB Logi Bolt&nbsp;</p>\r\n<p>- C&ocirc;ng nghệ Bluetooth tiết kiệm năng lượng</p>\r\n<p>- Phạm vi kh&ocirc;ng d&acirc;y: 10 m</p>\r\n<p>- Loại pin: 1 pin AA (đi k&egrave;m)</p>\r\n<p>- Thời gian sử dụng pin với đầu thu USB Logi Bolt: l&ecirc;n tới 24 th&aacute;ng&nbsp;</p>\r\n<p>- Thời gian sử dụng pin với Bluetooth: l&ecirc;n tới 20 th&aacute;ng&nbsp;</p>\r\n<p>- Phạm vi c&oacute; thể thay đổi t&ugrave;y theo việc sử dụng v&agrave; t&igrave;nh trạng sử dụng m&aacute;y t&iacute;nh</p>\r\n<p>- N&uacute;t 5 (Nhấp tr&aacute;i/phải, Quay lại/Tiếp tục, N&uacute;t cuộn với n&uacute;t nhấp chuột giữa)</p>\r\n<p>- Cảm biến: T&iacute;nh năng theo d&otilde;i quang học n&acirc;ng cao của Logitech</p>\r\n<p>- DPI: 400 up to 4000</p>\r\n<p>- Trọng lượng: 101,4 g - trọng lượng bao gồm pin (25g) v&agrave; bộ thu (1,8g)</p>\r\n<p>- K&iacute;ch thước</p>\r\n<p>&nbsp; &nbsp; &nbsp; - Chiều cao: 108,2 mm</p>\r\n<p>&nbsp; &nbsp; &nbsp; - Chi&ecirc;̀u r&ocirc;̣ng: 61,0 mm</p>\r\n<p>&nbsp; &nbsp; &nbsp; - Chiều d&agrave;y: 38,8 mm</p>\r\n<p>- Phần mềm t&ugrave;y chọn: Được hỗ trợ bởi Logitech Options tr&ecirc;n macOS 10.15 trở l&ecirc;n v&agrave; Windows&reg; 10, 11 trở l&ecirc;n</p>\r\n</body>\r\n</html>', 88, 'chuot-logitech', 1, 1, 'VINA', '2022-10-29 16:13:39', '2022-10-29 16:13:39', NULL, NULL);
INSERT INTO `vnc_product` VALUES (655, 'Bàn phím Logitech MX Keys Mini', 'ban-phim-logitech-mx-keys-mini', '<!DOCTYPE html>\r\n<html>\r\n<head>\r\n</head>\r\n<body>\r\n<p>- Logitech MX Keys Mini - Graphite l&agrave; một sản phẩm b&agrave;n ph&iacute;m Bluetooth nhỏ gọn sử dụng hằng ng&agrave;y. Chiếc b&agrave;n ph&iacute;m được thiết kế sang trọng v&agrave; đẹp mắt. Người d&ugrave;ng sẽ cảm nhận r&otilde; sự thoải m&aacute;i v&agrave; v&ocirc; c&ugrave;ng h&agrave;i l&ograve;ng về sản phẩm n&agrave;y.</p>\r\n<p><strong>K&iacute;ch thước nhỏ trải nghiệm thoải m&aacute;i</strong></p>\r\n<p>- Trải nghiệm kh&ocirc;ng gian l&agrave;m việc rộng r&atilde;i th&ocirc;ng tho&aacute;ng hơn với chiếc b&agrave;n ph&iacute;m Logitech MX Keys Mini - Graphite. Giảm khoảng c&aacute;ch giữa b&agrave;n ph&iacute;m v&agrave; chuột gi&uacute;p bạn thuận tiện trong thao t&aacute;c. Nhờ vậy, bạn c&oacute; thể điều chỉnh tư thế ngồi tốt hơn trong qu&aacute; tr&igrave;nh l&agrave;m việc.</p>\r\n<p><strong>Thao t&aacute;c mượt m&agrave;</strong></p>\r\n<p>- Đảm bảo mọi thao t&aacute;c g&otilde; ph&iacute;m đều mượt m&agrave;, tự nhi&ecirc;n v&agrave; ch&iacute;nh x&aacute;c. C&aacute;c ph&iacute;m bấm được thiết kế dạng h&igrave;nh cầu l&otilde;m tương th&iacute;ch với h&igrave;nh d&aacute;ng của từng đầu ng&oacute;n tay. C&aacute;c cạnh tr&ograve;n cộng th&ecirc;m lớp phủ mờ của ph&iacute;m nhấn đem lại sự thoải m&aacute;i, thao t&aacute;c lướt nhẹ nh&agrave;ng tr&ecirc;n bề mặt của ch&uacute;ng. To&agrave;n bộ phần th&acirc;n của MX Keys Mini - Graphite được kết nối với nhau bằng một tấm kim loại tạo n&ecirc;n cấu tr&uacute;c chắc chắn, ngăn b&agrave;n ph&iacute;m trượt tr&ecirc;n b&agrave;n.</p>\r\n<p><strong>Cảm biến th&ocirc;ng minh</strong></p>\r\n<p>- Cảm biến được sản xuất một c&aacute;ch th&ocirc;ng minh. Bạn sẽ dễ d&agrave;ng thao t&aacute;c nhấn phim kể cả khi trong điều kiện thiếu s&aacute;ng. Chiếc b&agrave;n ph&iacute;m được thiết lập &aacute;nh s&aacute;ng dựa v&agrave;o sự ph&aacute;t hiện khi tay bạn tiếp cận đến sản phẩm. Đồng thời sẽ tự động ngắt &aacute;nh s&aacute;ng tiết kiệm năng lượng khi bạn đi ra xa chiếc b&agrave;n ph&iacute;m của m&igrave;nh. Cường độ đ&egrave;n n&egrave;n tự động tương th&iacute;ch với điều kiện &aacute;nh s&aacute;ng hoặc bạn c&oacute; thể thiết lập thủ c&ocirc;ng.</p>\r\n<p><strong>Sạc nhanh hơn, kết nối nhanh hơn</strong></p>\r\n<p>- MX Keys Mini duy tr&igrave; thời lượng pin l&ecirc;n đến 10 ng&agrave;y sau mỗi lần sạc đầy - hoặc l&ecirc;n đến 5 th&aacute;ng khi tắt đ&egrave;n nền. Thời lượng pin c&oacute; thể thay đổi t&ugrave;y theo người d&ugrave;ng v&agrave; điều kiện m&aacute;y t&iacute;nh. Sử dụng c&aacute;p sạc USB-C để nạp th&ecirc;m năng lượng. Dễ d&agrave;ng gh&eacute;p nối MX Keys Mini với tối đa ba thiết bị c&ugrave;ng một l&uacute;c qua Bluetooth Low Energy, kh&ocirc;ng cần sử dụng hết cổng USB để kết nối.</p>\r\n<p><strong>Sử dụng ph&iacute;m th&ocirc;ng minh</strong></p>\r\n<p>- Chiếc b&agrave;n ph&iacute;m Logitech thiết kế 3 ph&iacute;m nhấn th&ocirc;ng minh: Emoji, Dictation v&agrave; Mic Mute/ Unmute. Thuận tiện để gửi biểu tượng cảm x&uacute;c, tắt tiếng v&agrave; bật tiếng micr&ocirc; của bạn cũng như k&iacute;ch hoạt t&iacute;nh năng n&oacute;i chuyện bằng văn bản.</p>\r\n<p>&nbsp;</p>\r\n</body>\r\n</html>', 87, 'ban-phim-logitech', 1, 1, 'VINA', '2022-11-05 18:02:29', '2022-11-05 18:02:29', NULL, NULL);
